delete PautaAssembleia/*************** Insert PautaAssembleia ***************/insert into PautaAssembleia (ID                            ,CreatedOn							,Descricao							,Assembleia							,Pauta)select ROW_NUMBER() OVER (ORDER BY Pauta.ID) ID     , GETDATE() CreatedOn     , Pauta.Descricao Descricao     , Assembleia.ID Assembleia     , Pauta.ID Pauta  from Pauta     , Assembleia;/*************** Insert OpcaoPautaAssembleia ***************/insert into OpcaoPautaAssembleia (ID                                 ,CreatedOn								 ,Descricao								 ,PautaAssembleia)select ROW_NUMBER() OVER (ORDER BY PautaAssembleia.ID) ID     , GETDATE() CreatedOn	 , OpcaoPauta.Descricao     , PautaAssembleia.ID PautaAssembleia  from PautaAssembleia     , OpcaoPauta where PautaAssembleia.Pauta = OpcaoPauta.Pauta;