       FETCH CECRED.BTCH0001.cr_crapdat INTO rw_crapdat;
       IF CECRED.BTCH0001.cr_crapdat%NOTFOUND THEN
         CLOSE CECRED.BTCH0001.cr_crapdat;
         RAISE_APPLICATION_ERROR(-20001,'Erro abrir data para cooperativa:' || vr_cdcooper || ' - ' || sqlerrm);
     ELSE
       CLOSE CECRED.BTCH0001.cr_crapdat;
     END IF;
    END IF;
    
    vr_cdagenci := vr_vet_dados(3);
    vr_cdbccxlt := vr_vet_dados(4);
    vr_nrdolote := vr_vet_dados(5);
    vr_nrdconta := vr_vet_dados(6);
    vr_nrdocmto := vr_vet_dados(9) || cecred.gene0002.fn_mask(TO_NUMBER(TO_CHAR(SYSTIMESTAMP, 'FF')),'9999999999');
    vr_cdhistor := vr_vet_dados(10);
    vr_vllanmto := vr_vet_dados(11);
    vr_nrdctabb := vr_vet_dados(7);
    vr_cdpesqbb := vr_vet_dados(14);
    
    cecred.lanc0001.pc_gerar_lancamento_conta(pr_cdcooper    => vr_cdcooper,
                                              pr_dtmvtolt    => rw_crapdat.dtmvtolt,
                                              pr_cdagenci    => vr_cdagenci,
                                              pr_cdbccxlt    => vr_cdbccxlt,
                                              pr_nrdolote    => vr_nrdolote,
                                              pr_nrdconta    => vr_nrdconta,
                                              pr_nrdocmto    => vr_nrdocmto,
                                              pr_cdhistor    => vr_cdhistor,
                                              pr_vllanmto    => vr_vllanmto,
                                              pr_nrdctabb    => vr_nrdctabb,
                                              pr_cdpesqbb    => vr_cdpesqbb,
                                              pr_flgperdb    => vr_flgperdb,
                                              pr_inprolot    => 1,
                                              pr_tab_retorno => vr_tab_retorno,
                                              pr_incrineg => vr_incrineg,
                                              pr_cdcritic => vr_cdcritic,
                                              pr_dscritic => vr_dscritic);
    
    IF (nvl(vr_cdcritic,0) <> 0 OR vr_dscritic IS NOT NULL) THEN
      ROLLBACK;
      RAISE_APPLICATION_ERROR(-20002,'Erro ao inserir lancamento - vr_cdcritic: ' || vr_cdcritic || ' / vr_dscritic: ' || vr_dscritic);
    END IF;
    
    vr_nrdrowid := null;
    
    CECRED.GENE0001.pc_gera_log(pr_cdcooper => vr_cdcooper,
                           pr_cdoperad => vr_cdoperad,
                           pr_dscritic => vr_dscritic,
                           pr_dsorigem => 'AIMARO',
                           pr_dstransa => vc_dstransa,
                           pr_dttransa => vr_dttransa,
                           pr_flgtrans => 1,
                           pr_hrtransa => vr_hrtransa,
                           pr_idseqttl => 0,
                           pr_nmdatela => NULL,
                           pr_nrdconta => vr_nrdconta,
                           pr_nrdrowid => vr_nrdrowid);
                           
    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.CDCOOPER',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_cdcooper);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.DTMVTOLT',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => rw_crapdat.dtmvtolt);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.CDAGENCI',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_cdagenci);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.CDBCCXLT',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_cdbccxlt);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.NRDOLOTE',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_nrdolote);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.NRDCTABB',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_nrdctabb);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.NRDOCMTO',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_nrdocmto);

    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.CDHISTOR',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_cdhistor);
    
    CECRED.GENE0001.pc_gera_log_item(pr_nrdrowid => vr_nrdrowid,
                                     pr_nmdcampo => 'craplcm.VLLANMTO',
                                     pr_dsdadant => null,
                                     pr_dsdadatu => vr_vllanmto);
    
    pos := pos + v_readSize_nr + 1;
    v_line_nr := v_line_nr + 1;
  END LOOP;      
  
  COMMIT;
  
Exception
  when others then
    RAISE_APPLICATION_ERROR(-20000,'Erro lancamento:' || sqlerrm);
end; 
 
